// src/data/index.js

const requireContext = require.context('./', false, /\.json$/);

const fileMappings = {};

requireContext.keys().forEach((fileName) => {
  const key = fileName.replace('./', '').replace('.json', '');
  fileMappings[key] = requireContext(fileName);
});

export default fileMappings;
