// store/index.js

import { createStore } from 'vuex';

export default createStore({
  state: {
    name: '',
    score: '',
    archives: [] // 新增的状态，用于存储档案列表
  },
  mutations: {
    setName(state, name) {
      state.name = name;
    },
    setScore(state, score) {
      state.score = score;
    },
    addArchive(state, archive) {
      state.archives.push(archive);
    }
  },
  actions: {
    createArchive({ commit, state }) {
      const archive = {
        name: state.name,
        score: state.score
      };
      commit('addArchive', archive);
    }
  }
});
