import { createStore } from 'vuex'
import { useStorage } from '@vueuse/core'

const studentInformation = {
  name: '',
  score: 0,
  studentId: '',
  subject: ''
}

const storedState = useStorage('student-information', studentInformation)

const store = createStore({
  state() {
    return {
      ...storedState.value,
      archivesList: JSON.parse(localStorage.getItem('archivesList')) || []
    }
  },
  mutations: {
    setName(state, name) {
      state.name = name
      storedState.value = state
    },
    setScore(state, score) {
      state.score = score
      storedState.value = state
    },
    setStudentId(state, studentId) {
      state.studentId = studentId
      storedState.value = state
    },
    setSubject(state, subject) {
      state.subject = subject
      storedState.value = state
    },
    addArchive(state, archive) {
      state.archivesList.push(archive)
      localStorage.setItem('archivesList', JSON.stringify(state.archivesList))
    },
    addToArchive(state, { archiveName, record }) {
      const archive = state.archivesList.find(archive => archive.name === archiveName)
      if (archive) {
        archive.data.push(record)
      } else {
        state.archivesList.push({ name: archiveName, data: [record] })
      }
      localStorage.setItem('archivesList', JSON.stringify(state.archivesList))
    }
  }
})

export default store
