// store/index.js

import { createStore } from 'vuex';

const ARCHIVES_KEY = 'archivesList';
const NAME_KEY = 'name';
const SCORE_KEY = 'score';

export default createStore({
  state: {
    name: localStorage.getItem(NAME_KEY) || '',
    score: localStorage.getItem(SCORE_KEY) || '',
    archivesList: JSON.parse(localStorage.getItem(ARCHIVES_KEY)) || []
  },
  mutations: {
    setName(state, name) {
      state.name = name;
      localStorage.setItem(NAME_KEY, name);
    },
    setScore(state, score) {
      state.score = score;
      localStorage.setItem(SCORE_KEY, score);
    },
    addArchive(state, archive) {
      state.archivesList.push(archive);
      localStorage.setItem(ARCHIVES_KEY, JSON.stringify(state.archivesList));
    },
    removeArchive(state, index) {
      state.archivesList.splice(index, 1);
      localStorage.setItem(ARCHIVES_KEY, JSON.stringify(state.archivesList));
    }
  },
  actions: {
    createArchive({ commit, state }) {
      const archive = {
        name: state.name,
        score: state.score,
        id: Date.now()
      };
      commit('addArchive', archive);
    },
    deleteArchive({ commit }, index) {
      commit('removeArchive', index);
    }
  }
});
