import { createStore } from 'vuex';
import createPersistedState from 'vuex-persistedstate';

export default createStore({
  state: {
    name: '',
    score: '',
    archivesList: JSON.parse(localStorage.getItem('archivesList')) || [],
  },
  mutations: {
    setName(state, name) {
      state.name = name;
      localStorage.setItem('name', name); // Persist name to localStorage
    },
    setScore(state, score) {
      state.score = score;
      localStorage.setItem('score', score); // Persist score to localStorage
    },
    addArchive(state, archive) {
      state.archivesList.push(archive);
      localStorage.setItem('archivesList', JSON.stringify(state.archivesList));
    },
    deleteArchive(state, index) {
      state.archivesList.splice(index, 1);
      localStorage.setItem('archivesList', JSON.stringify(state.archivesList));
    }
  },
  plugins: [createPersistedState({
    storage: window.localStorage, // Use localStorage instead of sessionStorage
  })],
});
